TITLE: Setting Vertex Indices for Primary Triangle
DESCRIPTION: Implements index assignment for vertices in the primary triangle, handling edge cases and rotations.
SOURCE: https://github.com/babylonjs/documentation/blob/master/content/guidedLearning/workshop/Geodesic_Code.md#2025-04-23_snippet_6

LANGUAGE: javascript
CODE:
```
Primary.prototype.SetIndices = function() {
    let indexCount = 12; // 12 vertices already assigned
    const vecToIdx = {}; //maps iso-vectors to indexCount;
    const m = this.m;
    const n = this.n;
    let g = m; // hcf of m, n when n != 0
    let m1 = 1;
    let n1 = 0;
    if (n !== 0) {
        g = HCF(m, n);
    };
    m1 = m / g;
    n1 = n / g;

    let fr = 0; //face to the right of current face
    let rot = ""; //rotation about which vertes for fr
    let O = 0;
    let A = 0;
    let B = 0;
    let OR = 0;
    let AR = 0;
    let BR = 0;
    const Ovec = new IsoVector(0, 0);
    const Avec = new IsoVector(m, n);
    const Bvec = new IsoVector(-n, m + n);
    let OAvec = new IsoVector(0, 0);
    let ABvec = new IsoVector(0, 0);
    let OBvec = new IsoVector(0, 0);
    let temp = 0;
    let tempR = 0;
    let verts = [];
    let idx = "";
    let idxR = "";


    /***edges AB to OB***** rotation about B*/
    for (let f = 0; f < 20; f++) { //f current face

        verts = IDATA.face[f];
        O = verts[2];
        A = verts[1];
        B = verts[0];

        idx = f +"|"+ Ovec.x + "|" + Ovec.y;
        if (!(idx in vecToIdx)) {
            vecToIdx[idx] = O;
        }

        idx = f +"|"+ Avec.x + "|" + Avec.y;
        if (!(idx in vecToIdx)) {
            vecToIdx[idx] = A;
        }
        idx = f +"|"+ Bvec.x + "|" + Bvec.y;
        if (!(idx in vecToIdx)) {
            vecToIdx[idx] = B;
        }
        fr = IDATA.edgematch[f][0];
        rot = IDATA.edgematch[f][1];
        if (rot === "B") {
                for (let i = 1; i < g; i++) {
                    ABvec.x = m - i * (m1 + n1);
                    ABvec.y = n + i * m1;
                    OBvec.x = -i * n1;
                    OBvec.y = i * (m1 + n1);
                    idx = f +"|"+ ABvec.x + "|" + ABvec.y;
                    idxR = fr +"|"+ OBvec.x + "|" + OBvec.y;
                    matchIdx(idx, idxR, "B");
                }
        };

       if (rot === "O") {
                for (let i = 1; i < g; i++) {
                    OBvec.x = -i * n1;
                    OBvec.y = i * (m1 + n1);
                    OAvec.x = i * m1;
                    OAvec.y = i * n1;
                    idx = f +"|"+ OBvec.x + "|" + OBvec.y;
                    idxR = fr +"|"+ OAvec.x + "|" + OAvec.y;
                    matchIdx(idx, idxR, "O");
                }
        };

        fr = IDATA.edgematch[f][2];
        rot = IDATA.edgematch[f][3];       
      if (rot && rot === "A") {
                for (let i = 1; i < g; i++) {
                    OAvec.x = i * m1;
                    OAvec.y = i * n1;
                    ABvec.x = m - (g - i) * (m1 + n1);;
                    ABvec.y = n + (g - i) * m1;
                    idx = f +"|"+ OAvec.x + "|" + OAvec.y;
                    idxR = fr +"|"+ ABvec.x + "|" + ABvec.y;
                    matchIdx(idx, idxR, "A");
                }
        };

        for (let i = 0; i < this.vertices.length; i++) {
            idx = f + "|" + this.vertices[i].x + "|" + this.vertices[i].y;
            if (!(idx in vecToIdx)) {
                vecToIdx[idx] = indexCount++;
            }
        } 
    };

    function matchIdx(idx, idxR, v) {
        if (!(idx in vecToIdx || idxR in vecToIdx )) {
            vecToIdx[idx] = indexCount;
            vecToIdx[idxR] = indexCount;
            indexCount++;
        }
        else if ((idx in vecToIdx) && !(idxR in vecToIdx)) {
            vecToIdx[idxR] = vecToIdx[idx];
        }
        else if ((idxR in vecToIdx) && !(idx in vecToIdx)) {
            vecToIdx[idx] = vecToIdx[idxR];
        }
    };

    this.vecToIdx = vecToIdx;
}
```

