    type: Object,
    default() {
      return { x: 0, y: 0, z: 0 };
    },
  },
},
data() {
  return {
    scene: null,
    engine: null,
  };
},
watch: {
  position(val) {
    if (val) {
      const box = this.scene.getMeshByName("box-red");
      const position = new Vector3(val.x, val.y, val.z);
      if (box) {
