      "type": "button",
      "icon": "iconfont icon-refresh",
      "tooltip": "",
      "label": "CRUD外层按钮",
      "level": "enhance",
      "onEvent": {
        "click": {
          "weight": 0,
          "actions": [
            {
              "componentId": "crudId",
              "ignoreError": false,
              "actionType": "reload",
              "dataMergeMode": "override",
              "data": {
              },
              "args": {
                "resetPage": true
              }
            }
          ]
        }
      }
    },
    {
      "type": "crud",
      "name": "crudName",
      "id": "crudId",
      "syncLocation": false,
      "api": "/api/mock2/crud/table",
      "headerToolbar": [
        "bulkActions",
        {
          "type": "reload",
          "align": "right",
          "icon": "iconfont icon-refresh",
          "label": "刷新(type)",
          "tooltip": "",
          "level": "primary"
        },
        {
          "type": "action",
          "align": "right",
          "icon": "iconfont icon-refresh",
          "label": "刷新(actionType)",
          "tooltip": "",
          "level": "primary",
          "actionType": "reload",
          "target": "crudName"
        },
        {
          "type": "button",
          "align": "right",
          "icon": "iconfont icon-refresh",
          "tooltip": "",
          "label": "事件动作(onEvent)",
          "level": "primary",
          "onEvent": {
            "click": {
              "weight": 0,
              "actions": [
                {
                  "componentId": "crudId",
                  "groupType": "component",
                  "actionType": "reload",
                  "dataMergeMode": "override"
                }
              ]
            }
          }
        }
      ],
      "bulkActions": [
        {
          "label": "批量删除",
          "actionType": "ajax",
          "api": "delete:/api/mock2/sample/${ids|raw}",
          "confirmText": "确定要批量删除?"
        },
        {
          "label": "批量修改",
          "actionType": "dialog",
