                {
                  "type": "hidden",
                  "name": "ids"
                },
                {
                  "type": "input-text",
                  "name": "engine",
                  "label": "Engine"
                }
              ]
            }
          }
        }
      ],
      "columns": [
          {
              "name": "id",
              "label": "ID"
          },
          {
              "name": "engine",
              "label": "Rendering engine"
          },
          {
              "name": "browser",
              "label": "Browser"
          },
          {
              "name": "platform",
              "label": "Platform(s)"
          },
          {
              "name": "version",
              "label": "Engine version"
          },
          {
              "name": "grade",
              "label": "CSS grade"
          }
      ]
  }
  ]
}
