    "url": "/api/mock2/form/saveForm",
    "graphql": "mutation AddUser($name: String!, $email: String!) { \
        insert_user(object: { name: $name, email: $email }) { \
          name \
          email \
        } \
    }"
  },
  "body": [
    {
      "type": "input-text",
      "name": "name",
      "label": "姓名："
    },
    {
      "name": "email",
      "type": "input-email",
      "label": "邮箱："
