            "multiple": true,
            "maxTagCount": 3,
            "overflowTagPopover": {
              "title": "已选项"
            },
            "value": "Apple,Banana,Blackberry,Blueberry,Cherry,Carambola,Coconut,Kiwifruit,Lemon,Pineapple,Vegetables,Wheat,Rice",
