  "data": {
    "table": [
      {
        "id": 1,
        "a": "a1",
        "b": "b1"
      }
    ]
  },
  "body": [
    {
      "showIndex": true,
      "type": "input-table",
      "name": "table",
      "columns": [
        {
          "name": "a",
          "label": "A"
