const schema = {
  type: 'form',
  api: {
    method: 'post',
    url: '/api/mock2/form/saveForm',
    requestAdaptor: function (api, context) {
      console.log(context); // 打印上下文数据

      return {
        ...api,
        data: {
          ...api.data, // 获取暴露的 api 中的 data 变量
          foo: 'bar' // 新添加数据
        }
      };
    }
  },
  body: [
    {
      type: 'input-text',
      name: 'name',
      label': "姓名："
    },
    {
      name: 'text',
      type: 'input-email',
      "label": "邮箱："
    }
  ]
};
```

