        "style": {
          "width": "100%"
        },
        "onEvent": {
          "change": {
            "actions": [
              {
                "componentId": "tree",
                "groupType": "component",
                "actionType": "search",
                "args": {
                  "keyword": "${event.data.value}"
                }
              }
            ]
          }
        }
      },
      {
        "type": "input-tree",
        "id": "tree",
        "name": "tree",
