[ { "type": "button-toolbar", "className": "m-b", "buttons": [ { "name": "trigger3", "id": "trigger3", "type": "action", "label": "清空列表全部选中项", "onEvent": { "click": { "actions": [ { "actionType": "clearAll", "componentId": "list-select", "description": "点击设置指定列表全部选中项清空" } ] } } } ] }, { "type": "service", "api": "/api/mock2/sample?perPage=10", "body": [ { "id": "list-select", "type": "list", "source": "$rows", "selectable": true, "multiple": true, "listItem": { "body": [ { "type": "hbox", "columns": [ { "label": "Engine", "name": "engine" }, { "name": "version", "label": "Version" } ] } ] } } ] } ]
