      {
        "id": 1,
        "a": "a1",
        "b": "b1"
      }
    ]
  },
  "body": [
    {
      "showIndex": true,
      "type": "input-table",
      "name": "table",
      "columns": [
        {
          "name": "a",
          "label": "A"
        },
        {
          "name": "b",
          "label": "B"
        }
      ],
      "removable": true,
      "needConfirm": false,
      "deleteApi": "/api/mock2/table/deleteFail",
      "onEvent": {
        "deleteFail": {
          "actions": [
            {
              "actionType": "toast",
              "args": {
                "msgType": "info",
                "position": "top-right",
                "title": "deleteFail事件",
                "msg": "上下文 ${event.data | json:0}"
