<script setup>
import { ref } from 'vue'

const nodes = ref([/** ... */])

const edges = ref([/** ... */])

const connector = (params) => {
  if (params.source === params.target) {
    return false
  }

  return {
    id: `edge-${params.source}-${params.target}`,
    source: params.source,
    target: params.target,
