  "redemption_type": "AUTOMATIC",
  "rules": [
    {
      "action": {
        "cart_items": {
          "discount": {
            "percentage_amount": 50
          },
          "items": {
            "categories": [
              36
            ]
          }
        }
      },
      "apply_once": true
    },
    {
      "action": {
        "cart_items": {
          "discount": {
            "percentage_amount": 40
          },
          "items": {
            "not": {
              "categories": [
