                  "displayName": "Size UK",
                  "values": [
                    {
                      "id": "bc/store/productOptionValue/68",
                      "label": "Small UK"
                    },
                    {
                      "id": "bc/store/productOptionValue/69",
                      "label": "Medium UK"
                    }
                  ]
                }
              }
            },
            {
              "node": {
                "id": "bc/store/productOption/109",
                "displayName": "Color",
                "isShared": false,
                "values": [
                  {
                    "id": "bc/store/productOptionValue/7",
                    "label": "Silver",
                    "isDefault": false
                  },
                  {
                    "id": "bc/store/productOptionValue/8",
                    "label": "Black",
                    "isDefault": false
