TITLE: Using ImagePicker in React Native
DESCRIPTION: Example of how to use the ImagePicker component in a React Native application. This code demonstrates picking an image from the camera roll and displaying it.
SOURCE: https://github.com/bluesky-social/expo/blob/main/docs/pages/versions/v49.0.0/sdk/imagepicker.mdx#2025-04-22_snippet_1

LANGUAGE: typescript
CODE:
```
import { useState, useEffect } from 'react';
import { Button, Image, View, Platform } from 'react-native';
import * as ImagePicker from 'expo-image-picker';

export default function ImagePickerExample() {
  const [image, setImage] = useState<string | null>(null);

  const pickImage = async () => {
    // No permissions request is necessary for launching the image library
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    console.log(result);

    if (!result.canceled) {
      setImage(result.assets[0].uri);
    }
  };

  return (
    <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      <Button title="Pick an image from camera roll" onPress={pickImage} />
      {image && <Image source={{ uri: image }} style={{ width: 200, height: 200 }} />}
    </View>
  );
}
```

