TITLE: Implementing Conditional Findings with Response Interceptor
DESCRIPTION: Full example showing how to implement conditional Findings that trigger only on specific response conditions, including error handling and logging. This example creates Findings only for HTTP 200 responses.
SOURCE: https://github.com/caido/doc-developer/blob/main/src/guides/components/findings.md#2025-04-20_snippet_2

LANGUAGE: typescript
CODE:
```
import type { DefineAPI, SDK } from "caido:plugin";
import type { Request, Response } from "caido:utils";

export type API = DefineAPI<{
  // No API methods needed for this passive functionality.
}>;

export function init(sdk: SDK<API>) {
  // Listen for intercepted responses.
  sdk.events.onInterceptResponse(
    async (
      sdk: SDK<API>, 
      request: Request, 
      response: Response
    ) => {
    try {
      // Only create Findings for 200 responses.
      if (response.getCode() === 200) {
        await sdk.findings.create({
          title: `Success Response ${response.getCode()}`,
          description: `Request ID: ${request.getId()}\nResponse Code: ${response.getCode()}`,
          reporter: "Response Logger Plugin",
          request: request,
          dedupeKey: `${request.getPath()}-${response.getCode()}`
        });

        sdk.console.log(`Created finding for successful request ${request.getId()}`);
      }
    } catch (err) {
      sdk.console.error(`Error creating finding: ${err}`);
    }
  });
}
