TITLE: Frontend Plugin Implementation with UI Components
DESCRIPTION: Implements a frontend plugin that creates a page with input fields and a button to call the multiply endpoint. Includes UI setup, event handling, and sidebar integration.
SOURCE: https://github.com/caido/doc-developer/blob/main/src/guides/components/rpc.md#2025-04-20_snippet_1

LANGUAGE: typescript
CODE:
```
import type { Caido } from "@caido/sdk-frontend";
import type { API } from "../../backend/src/index.ts";

export type CaidoSDK = Caido<API>;

const createPage = (sdk: CaidoSDK) => {

    const resultText = document.createElement("p");
    resultText.textContent = "Result will appear here.";

    const inputA = document.createElement("input");
    inputA.type = "number";
    inputA.value = "0";
    inputA.style.color = "black";

    const inputB = document.createElement("input");
    inputB.type = "number";
    inputB.value = "0";
    inputB.style.color = "black";

    const calculateButton = sdk.ui.button({
        variant: "primary",
        label: "Calculate",
    });

    calculateButton.addEventListener("click", async () => {
        const a = Number(inputA.value);
        const b = Number(inputB.value);
        const result = await sdk.backend.multiply(a, b);
        resultText.textContent = `Result: ${result}`;
    });

    const container = document.createElement("div");
    container.appendChild(inputA);
    container.appendChild(inputB);
    container.appendChild(calculateButton);
    container.appendChild(resultText);

    const card = sdk.ui.card({
        body: container
    });

    sdk.navigation.addPage("/multiply-page", {
        body: card
    });
}

export function init(sdk: CaidoSDK) {
    createPage(sdk);

    sdk.sidebar.registerItem("Multiply", "/multiply-page", {
        icon: "fas fa-calculator"
    });
}
```

