if [ -f "cake.config" ]; then

  cfg_parser cake.config
  cfg_section_Paths

  if [ ! -z ${Tools+x} ]; then

    # Make absolute and normalise
    TOOLS_DIR=$( python -c "import os,sys; print os.path.realpath(sys.argv[1])" $Tools)

    echo "Parsed cake.config. TOOLS_DIR updated to $TOOLS_DIR"
  fi
fi
```

