import { useBottomTabBarHeight } from 'react-native-bottom-tabs';

function MyComponent() {
  const tabBarHeight = useBottomTabBarHeight();

  return (
    <ScrollView>
      {/* Content */}
      <View style={{ bottom: tabBarHeight }} />
