{-# LANGUAGE TemplateHaskell #-}

import Data.Git.Revision.TH
  ( gitRevParseHEAD )

main :: IO ()
main = do
  let revHEAD = $(gitRevParseHEAD)
  putStrLn $ "revision: " <> revHEAD
```

