            alert("Couldn't connect to wallet");
        } else {
            provider.getActivePublicKey().then(publicKey => {
                props.setPublicKey(publicKey);
            }).catch(error => {
                alert(error.message);
            });
        }
    })
    .catch(error => {
        alert(error.message);
    });
}
