{
    "description": "An entry in the validator map.",
    "type": "object",
    "required": [
        "bonding_purse",
        "delegation_rate",
        "inactive",
        "maximum_delegation_amount",
        "minimum_delegation_amount",
        "staked_amount",
        "validator_public_key"
    ],
    "properties": {
        "validator_public_key": {
            "description": "Validator public key",
