        "description": "Named arguments to a contract.",
        "type": "array",
        "items": [
            {
                "type": "string"
            },
            {
                "$ref": "#/components/schemas/CLValue"
            }
        ],
        "maxItems": 2,
        "minItems": 2
    }
}

