            "description": "Execution should be scheduled for the specified timestamp or later.",
            "type": "object",
            "required": [
                "FutureTimestamp"
            ],
            "properties": {
                "FutureTimestamp": {
                    "$ref": "#/components/schemas/Timestamp"
                }
            },
            "additionalProperties": false
        }
