```
"TransactionScheduling": {
  "description": "Scheduling mode of a Transaction.",
  "oneOf": [
    {
      "description": "No special scheduling applied.",
      "type": "string",
      "enum": [
        "Standard"
      ]
    },
    {
      "description": "Execution should be scheduled for the specified era.",
      "type": "object",
      "required": [
        "FutureEra"
