  "examples": [
    {
      "name": "info_get_peers_example",
      "params": [],
      "result": {
        "name": "info_get_peers_example_result",
        "value": {
          "api_version": "2.0.0",
          "peers": [
            {
              "node_id": "tls:0101..0101",
              "address": "127.0.0.1:54321"
