                    }
                }
            },
            "additionalProperties": false
        },
        {
            "description": "Adds the given `i32`.",
            "type": "object",
            "required": [
                "AddInt32"
            ],
            "properties": {
                "AddInt32": {
                    "type": "integer",
                    "format": "int32"
                }
            },
            "additionalProperties": false
        },
        {
            "description": "Adds the given `u64`.",
