{\n\"Account\": {\n   \"account_address\": \"account-hash-a1\",\n   \"action_thresholds\": {\n         \"deployment\": 1,\n         \"key_management\": 2\n   },\n   \"associated_keys\": [\n      {\n         \"account_address\": \"account-hash-a1\", // primary key for key management\n         \"weight\": 2\n      },\n      {\n         \"account_address\": \"account-hash-b2\", // associated key used for deploys\n         \"weight\": 1\n      }\n   ],\n   \"main_purse\": \"uref-1234\",\n   \"named_keys\": []\n}\n}
