 * Determines whether a language's text direction is LTR or RTL.
 *
 * @param {string} language Language code.
 * @returns {String} Text direction. Either `'ltr'` or `'rtl'`.
 */
export function getLanguageDirection( language ) {
	const lang = language.toLowerCase();

	return LANGUAGE_CODES_WITH_RTL_DIRECTION.includes( lang ) ? 'rtl' : 'ltr';
}
```

