#include <stdarg.h>
#include <stdbool.h>
#include <stddef.h>
#include <stdint.h>
#include <stdlib.h>

typedef uint32_t Buffer;
typedef uint32_t BufferIter;

void _console_log(
    uint8_t level,
    const uint8_t *target,
    size_t target_len,
    const uint8_t *filename,
    size_t filename_len,
    uint32_t line_number,
    const uint8_t *text,
    size_t text_len
);

Buffer _buffer_alloc(
    const uint8_t *data,
    size_t data_len
);
void _buffer_consume(
    Buffer bufh,
    uint8_t *into,
    size_t len
);
size_t _buffer_len(Buffer bufh);

void _schedule_reducer(
    const uint8_t *name,
    size_t name_len,
    const uint8_t *args,
    size_t args_len,
    uint64_t time,
    uint64_t *out
);
void _cancel_reducer(uint64_t id);

uint16_t _create_index(
    const uint8_t *index_name,
    size_t index_name_len,
    uint32_t table_id,
    uint8_t index_type,
    const uint8_t *col_ids,
    size_t col_len
);
```

