    const codama = createFromJson(jsonIdl);
} catch (e) {
    if (isCodamaError(e, CODAMA_ERROR__UNEXPECTED_NODE_KIND)) {
        const { expectedKinds, kind, node } = e.context;
        // ...
    } else if (isCodamaError(e, CODAMA_ERROR__VERSION_MISMATCH)) {
