if error != nil return (trustedState, ErrRequestFailed)

untrustedNextVs, error := Validators(untrustedHeight + 1)
if error != nil return (trustedState, ErrRequestFailed)

error = verifySingle(
         trustedState,
         untrustedSh,
         untrustedVs,
         untrustedNextVs,
         trustThreshold)

if fatalError(error) return (trustedState, error)

