error, newTrustedState = VerifyBisection(pivotHeight,
                                         trustedState,
                                         trustThreshold,
                                         trustingPeriod,
                                         clockDrift,
                                         now)
if error != nil return (newTrustedState, error)

return VerifyBisection(untrustedHeight,
                       newTrustedState,
