function StartRound(round) {
 round_p  round
 step_p  propose
 if proposer(h_p, round_p) = p {
  // new wait condition
  wait until now_p > block time of block h_p - 1
  if validValue_p != nil {
   // add "now_p"
   proposal  (validValue_p, now_p) 
  } else {
   // add "now_p"
   proposal  (getValue(), now_p) 
  }
  broadcast PROPOSAL, h_p, round_p, proposal, validRound_p
