TITLE: Defining gRPC Service for External Secret Store Plugin
DESCRIPTION: This protobuf snippet defines the gRPC service interface for the External Secret Store plugin. It includes methods for getting, applying, and deleting secrets, along with the necessary message definitions.
SOURCE: https://github.com/crossplane/crossplane/blob/main/design/one-pager-pluggable-secret-stores.md#2025-04-21_snippet_1

LANGUAGE: protobuf
CODE:
```
syntax = "proto3";

// This service defines the APIs for an External Secret Store plugin.
service ExternalSecretStoreService {
    rpc GetSecret(GetSecretRequest) returns (GetSecretResponse) {}
    rpc ApplySecret(ApplySecretRequest) returns (ApplySecretResponse) {}
    rpc DeleteKeys(DeleteKeysRequest) returns (DeleteKeysResponse) {}
}

message ConfigReference {
    string api_version = 1;
    string kind = 2;
    string name = 3;
}
message Secret {
    string scoped_name = 1;
    map<string, string> metadata = 2;
    map<string, bytes> data = 3;
}
message GetSecretRequest {
    ConfigReference config = 1;
    Secret secret = 2;
    GetOptions options = 3;
}
message GetSecretResponse {
    Secret secret = 1;
}
message ApplySecretRequest {
    ConfigReference config = 1;
    Secret secret = 2;
    ApplyOptions options = 3;
}
message ApplySecretResponse {
    bool changed = 1;
}
message DeleteKeysRequest {
    ConfigReference config = 1;
    Secret secret = 2;
    DeleteOptions options = 3;
}
message DeleteKeysResponse {}
message GetOptions {}
message ApplyOptions {}
message DeleteOptions {
    bool keep_empty_secret = 1;
}
