import { defineConfig } from 'cypress'\n\nexport default defineConfig({\n  component: {\n    devServer(cypressConfig: CypressConfiguration) {\n      // return devServer instance or a promise that resolves to\n      // a dev server here\n      return {\n        port: 1234,\n        close: () => {},\n      }\n    },\n  },\n})
```

