TITLE: Building and Containerizing a Go Application
DESCRIPTION: This snippet demonstrates a Dagger Function that builds and containerizes a Go application. It shows how to create a container, install build tools, copy source code, compile the application, and create a runtime container.
SOURCE: https://github.com/dagger/dagger.git/blob/main/docs/current_docs/features/programmable-pipelines.mdx#2025-04-16_snippet_0

LANGUAGE: go
CODE:
```
package main

import (
	"context"

	"dagger.io/dagger"
)

func main() {
	ctx := context.Background()

	// initialize Dagger client
	client, err := dagger.Connect(ctx, dagger.WithLogOutput(os.Stderr))
	if err != nil {
		panic(err)
	}
	defer client.Close()

	// get reference to the local project
	src := client.Host().Directory(".", dagger.HostDirectoryOpts{
		Exclude: []string{"ci", "node_modules"},
	})

	// build application
	container, err := buildContainer(ctx, client, src)
	if err != nil {
		panic(err)
	}

	// get reference to container image
	containerID, err := container.ID(ctx)
	if err != nil {
		panic(err)
	}

	fmt.Println("Container built with ID:", containerID)
}

// buildContainer returns a reference to an OCI-compliant container image
// with a Go application pre-installed.
func buildContainer(ctx context.Context, client *dagger.Client, src *dagger.Directory) (*dagger.Container, error) {
	return client.Container().From("golang:1.19").
		// mount source code into container
		WithMountedDirectory("/src", src).
		WithWorkdir("/src").

		// install application dependencies
		WithExec([]string{"go", "mod", "download"}).

		// build application
		WithExec([]string{"go", "build", "-o", "app", "main.go"}).

		// create new container with just the built application
		With(func(c *dagger.Container) *dagger.Container {
			return client.Container().From("gcr.io/distroless/static").
				WithFile("/app", c.File("/src/app"))
		}).

		// set the entrypoint
		WithEntrypoint([]string{"/app"}).
		Sync(ctx)
}
```

LANGUAGE: python
CODE:
```
import os
import sys
import dagger


@dagger.function
async def build_container(src: dagger.Directory) -> dagger.Container:
    """Returns an OCI-compliant container with the Go application pre-installed."""
    # get Go build container
    ctr = dagger.container().from_("golang:1.19")

    # mount source code
    ctr = ctr.with_mounted_directory("/src", src).with_workdir("/src")

    # install dependencies
    ctr = ctr.with_exec(["go", "mod", "download"])

    # build application
    ctr = ctr.with_exec(["go", "build", "-o", "app", "main.go"])

    # create a new container with minimal runtime
    distroless = dagger.container().from_("gcr.io/distroless/static")
    return distroless.with_file("/app", ctr.file("/src/app")).with_entrypoint(["/app"])


@dagger.function
async def build() -> str:
    # get reference to the local project
    src = dagger.host().directory(".", exclude=["ci", "node_modules"])

    # build application
    ctr = await build_container(src)

    # get container ID
    return await ctr.id()
```

