import { connect } from "@dagger.io/dagger"

// initialize Dagger client
connect(
  async (client) => {
    // get reference to the local project
    const source = client.host().directory(".", {
      exclude: ["ci", "node_modules"],
    })

    // build container
    const container = await buildContainer(client, source)

    // get reference to container image
    const containerId = await container.id()

    console.log(`Container built with ID: ${containerId}`)
  },
