// buildContainer returns a reference to an OCI-compliant container image
// with a Go application pre-installed
async function buildContainer(client, src) {
  return client.container()
    .from("golang:1.19")
    // mount source code into container
    .withMountedDirectory("/src", src)
    .withWorkdir("/src")

    // install application dependencies
    .withExec(["go", "mod", "download"])

    // build application
    .withExec(["go", "build", "-o", "app", "main.go"])

    // create new container with just the built application
    .with(() => {
      return client.container()
        .from("gcr.io/distroless/static")
        .withFile("/app", client.container()
        .from("golang:1.19")
        .withMountedDirectory("/src", src)
        .withWorkdir("/src")
        .withExec(["go", "build", "-o", "app", "main.go"])
        .file("/src/app"))
    })

    // set the entrypoint
    .withEntrypoint(["/app"])
}
