<?php

namespace MyNamespace;

class MyModule
{
    #[\Dagger\Client\Function_\Doc('Returns an OCI-compliant container with a Go application pre-installed')]
    #[\Dagger\Client\Function_\Name('buildContainer')]
    public function buildContainer(\Dagger\Client\Client $client, \Dagger\Client\Directory $src): \Dagger\Client\Container
    {
        return $client->container()->from('golang:1.19')
            // mount source code into container
            ->withMountedDirectory('/src', $src)
            ->withWorkdir('/src')

            // install application dependencies
            ->withExec(['go', 'mod', 'download'])

            // build application
            ->withExec(['go', 'build', '-o', 'app', 'main.go'])

            // create new container with just the built application
            ->with(function (\Dagger\Client\Container $c) use ($client) {
                return $client->container()->from('gcr.io/distroless/static')
                    ->withFile('/app', $c->file('/src/app'));
            })

            // set the entrypoint
            ->withEntrypoint(['/app']);
    }

    #[\Dagger\Client\Function_\Doc('Builds a container with the application')]
    #[\Dagger\Client\Function_\Name('build')]
    public function build(\Dagger\Client\Client $client): string
    {
        // get reference to the local project
        $src = $client->host()->directory('.', [
            'exclude' => ['ci', 'node_modules'],
        ]);

        // build application
        $container = $this->buildContainer($client, $src);

        // get container ID
        return $container->id();
    }
}
