import anyio
import dagger
from dagger import dag, function, object_type
from google_cloud_run import GoogleCloudRun

@object_type
class MyModule:
    @function
    async def deploy(
        self,
        project_name: str,
        service_location: str,
        image_address: str,
        service_port: int,
        credential: dagger.Secret,
    ) -> str:
        gcr = GoogleCloudRun()

        service = await gcr.create_service(
            project_name,
            service_location,
            image_address,
            service_port,
            credential,
        )

        return f"Application deployed to {service}"


if __name__ == "__main__":
    anyio.run(dag.compute)
