TITLE: Implementing CI Pipeline in Go
DESCRIPTION: Go code for implementing a CI pipeline with four Dagger Functions: publish, test, build, and build-env.
SOURCE: https://github.com/dagger/dagger.git/blob/main/docs/current_docs/quickstart/ci/index.mdx#2025-04-16_snippet_2

LANGUAGE: go
CODE:
```
package main

import (
	"context"
	"fmt"
	"strings"
	"time"

	"dagger.io/dagger"
)

type HelloDagger struct{}

func (m *HelloDagger) Publish(ctx context.Context) (string, error) {
	image, err := m.Build(ctx)
	if err != nil {
		return "", err
	}

	tag := fmt.Sprintf("ttl.sh/hello-dagger:%d", time.Now().UnixMilli())
	_, err = image.Publish(ctx, tag)
	if err != nil {
		return "", err
	}

	return tag, nil
}

func (m *HelloDagger) Test(ctx context.Context) (string, error) {
	return m.BuildEnv(ctx).WithExec([]string{"npm", "run", "test"}).Stdout(ctx)
}

func (m *HelloDagger) Build(ctx context.Context) (*dagger.Container, error) {
	client, err := dagger.Connect(ctx)
	if err != nil {
		return nil, err
	}
	defer client.Close()

	src := client.Host().Directory(".")

	return client.Container().From("nginx:1.25-alpine").WithDirectory("/usr/share/nginx/html", m.BuildEnv(ctx).WithExec([]string{"npm", "run", "build"}).Directory("./dist")), nil
}

func (m *HelloDagger) BuildEnv(ctx context.Context) *dagger.Container {
	client, err := dagger.Connect(ctx)
	if err != nil {
		panic(err)
	}
	defer client.Close()

	src := client.Host().Directory(".")

	return client.Container().From("node:20-slim").WithDirectory("/src", src).WithWorkdir("/src").WithExec([]string{"npm", "ci"})
}
```

