import { dag, object, func, field } from "@dagger.io/dagger"
import { GoogleCloudRun } from "google-cloud-run"

@object()
class MyModule {
  @func()
  async deploy(
    @field projectName: string,
    @field serviceLocation: string,
    @field imageAddress: string,
    @field servicePort: number,
    @field credential: string
  ): Promise<string> {
    const gcr = new GoogleCloudRun()

    const service = await gcr.createService(
      projectName,
      serviceLocation,
      imageAddress,
      servicePort,
      dag.sensitiveString(credential)
    )

    return `Application deployed to ${service}`
  }
}
