import os
import sys
import dagger


@dagger.function
async def build_binary(src: dagger.Directory) -> dagger.File:
    """Builds a Go binary and returns a reference to the output file."""
    # get Go build container
    ctr = dagger.container().from_("golang:1.19")

    # mount source code
    ctr = ctr.with_mounted_directory("/src", src).with_workdir("/src")

    # install dependencies
    ctr = ctr.with_exec(["go", "mod", "download"])

    # build application and return file reference
    return ctr.with_exec(["go", "build", "-o", "app", "main.go"]).file("/src/app")


@dagger.function
async def build() -> str:
    # get reference to the local project
    src = dagger.host().directory(".", exclude=["ci", "node_modules"])

    # build application
    output_file = await build_binary(src)

    # export file to host
    output_path = await output_file.export("./app")

    return f"Binary exported to: {output_path}"
