package io.dagger.modules.mymodule;

import io.dagger.client.Container;
import io.dagger.client.Directory;
import io.dagger.client.File;
import io.dagger.client.Function;
import io.dagger.client.Doc;
import io.dagger.client.Client;

import java.util.List;

public class MyModule {

    /**
     * Builds a Go binary and returns a reference to the output file
     */
    @Function
    public File buildBinary(Directory src) {
        return client.container().from("golang:1.19")
            // mount source code into container
            .withMountedDirectory("/src", src)
            .withWorkdir("/src")

            // install application dependencies
            .withExec(List.of("go", "mod", "download"))

            // build application
            .withExec(List.of("go", "build", "-o", "app", "main.go"))

            // get reference to output file
            .file("/src/app");
    }

    /**
     * Builds and exports a Go binary
     */
    @Function
    public String build() {
        // get reference to the local project
        Directory src = client.host().directory(".", d -> d.exclude(List.of("ci", "node_modules")));

        // build application
        File outputFile = buildBinary(src);

        // export file to host
        String outputPath = outputFile.export("./app");

        return "Binary exported to: " + outputPath;
    }

    private final Client client;

    public MyModule(Client client) {
        this.client = client;
    }
}
