type Greeter struct {
	Name     string
	Greeting string
}

func (m *MyModule) WithName(name string) *Greeter {
	return &Greeter{Name: name, Greeting: "Hello"}
}

func (m *MyModule) WithGreeting(g *Greeter, greeting string) *Greeter {
	g.Greeting = greeting
