LANGUAGE: java
CODE:
```
package io.dagger.modules.mymodule;

import io.dagger.dagger.*;
import io.dagger.dagger.kotlinx.*;

import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;

import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.databind.JsonNode;

@DaggerModule
public class MyModule {

    @DaggerFunction
    public User getUser(String gender) throws Exception {
        HttpClient client = HttpClient.newHttpClient();
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create("https://randomuser.me/api/?gender=" + gender))
                .build();

        HttpResponse<String> response = client.send(request, HttpResponse.BodyHandlers.ofString());

        ObjectMapper mapper = new ObjectMapper();
        JsonNode root = mapper.readTree(response.body());
        JsonNode results = root.get("results");

        if (results.size() == 0) {
            throw new RuntimeException("No user found");
        }

        JsonNode user = results.get(0);
        JsonNode name = user.get("name");

        return new User(
            name.get("title").asText(),
            name.get("first").asText(),
            name.get("last").asText()
        );
    }

    public static class User {
        public String title;
        public String first;
        public String last;

        public User(String title, String first, String last) {
            this.title = title;
            this.first = first;
            this.last = last;
        }
    }
}
```

