TITLE: Implementing Authentication Middleware
DESCRIPTION: Middleware for protecting routes and handling authentication state
SOURCE: https://github.com/denoland/fresh/blob/main/docs/latest/examples/authentication-with-supabase.md#2025-04-19_snippet_7

LANGUAGE: typescript
CODE:
```
import { FreshContext } from "$fresh/server.ts";
import { createSupabaseClient } from "lib/supabase.ts";

export const handler = [
  async function authMiddleware(req: Request, ctx: FreshContext) {
    const url = new URL(req.url);
    const headers = new Headers();
    headers.set("location", "/");

    const supabase = createSupabaseClient(req, headers);
    const { error, data: { user } } = await supabase.auth.getUser();

    const isProtectedRoute = url.pathname.includes("secret");

    if (error && error.status !== 401) throw error;

    if (isProtectedRoute && !user) {
      return new Response(null, { status: 303, headers });
    }

    ctx.state.user = user;

    return ctx.next();
  },
];
```

