TITLE: Creating Password Reset Form with Descope SDK in React
DESCRIPTION: A React component for password reset utilizing Descope SDK. The form handles the initial email submission to trigger a reset code, then allows the user to input the code and create a new password.
SOURCE: https://github.com/descope/descope-js/blob/main/packages/libs/escape-markdown/README.md#2025-04-22_snippet_2

LANGUAGE: jsx
CODE:
```
import { useState } from 'react';
import { useDescope } from '@descope/react-sdk';

export default function CustomPasswordResetForm() {
  const { sdk } = useDescope();

  // Track the step of the reset process
  const [step, setStep] = useState('request'); // 'request', 'reset', 'success'
  const [email, setEmail] = useState('');
  const [code, setCode] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [error, setError] = useState('');

  const handleRequestReset = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Request password reset
      await sdk.password.sendPasswordReset(email);
      setStep('reset');
    } catch (err) {
      setError(err.message || 'Failed to request password reset. Please try again.');
      console.error('Password reset request error:', err);
    }
  };

  const handleResetPassword = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Verify the code and set the new password
      await sdk.password.resetPassword(email, code, newPassword);
      setStep('success');
    } catch (err) {
      setError(err.message || 'Failed to reset password. Please check your code and try again.');
      console.error('Password reset error:', err);
    }
  };

  if (step === 'success') {
    return (
      <div className="success-message">
        <h2>Password Reset Successful!</h2>
        <p>Your password has been updated. You can now login with your new password.</p>
        {/* Add a link to your login page */}
      </div>
    );
  }

  return (
    <div className="custom-form">
      <h2>Reset Your Password</h2>
      {error && <div className="error-message">{error}</div>}

      {step === 'request' ? (
        <form onSubmit={handleRequestReset}>
          <p>Enter your email address to receive a password reset code.</p>
          <div className="form-group">
            <label htmlFor="email">Email:</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="submit-button">
            Request Reset Code
          </button>
        </form>
      ) : (
        <form onSubmit={handleResetPassword}>
          <p>Check your email for the reset code and enter it below along with your new password.</p>

          <div className="form-group">
            <label htmlFor="code">Reset Code:</label>
            <input
              id="code"
              type="text"
              value={code}
              onChange={(e) => setCode(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="newPassword">New Password:</label>
            <input
              id="newPassword"
              type="password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
            />
          </div>

          <button type="submit" className="submit-button">
            Reset Password
          </button>
        </form>
      )}
    </div>
  );
}
```

