TITLE: Implementing Login Form and Authentication in Next.js
DESCRIPTION: React component code for a login form and authentication logic using Directus SDK. This snippet includes state management for credentials and refresh token, as well as functions for handling login and WebSocket setup.
SOURCE: https://github.com/directus/docs/blob/main/content/tutorials/2.projects/build-a-realtime-chat-app-with-directus-and-next.md#2025-04-09_snippet_4

LANGUAGE: javascript
CODE:
```
'use client';
import { useState, useEffect } from 'react';
import directus from '@/lib/directus';

export default function Home() {
  const [credentials, setCredentials] = useState({
    email: '',
    password: '',
  });
  const [refreshToken, setRefreshToken] = useState(undefined);

  // Handle login via WebSocket
  const login = async () => {
    const authResponse = await directus.login(credentials.email, credentials.password, {
      mode: "json",
    });
    console.log("Login successful", authResponse);

    setupWebSocket()
  };

  // Connects the realtime client, authenticates via handshake, and sets up the message listener
  async function setupWebSocket() {
    await directus.connect();

    directus.sendMessage({
      type: "auth",
      email: credentials.email,
      password: credentials.password,
    })

    directus.onWebSocket('message', (message) => {
      try {
        console.log('Received WebSocket message:', message);
        if (message.type === 'auth' && message.status === 'ok') {
          setRefreshToken(message.refresh_token);
        }
      } catch (err) {
        console.error('Error parsing WebSocket message:', err);
      }
    });
    directus.onWebSocket('error', (err) => {
      console.error('WebSocket error:', err);
    });
  }

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCredentials((prev) => ({
      ...prev,
      [name]: value,
    }));
  };
  return (
    <div>
      <h1>Directus Realtime Chat</h1>
      {refreshToken === undefined ? (
        <div>
          <h2>Login</h2>
          <input
            type="text"
            name="email"
            placeholder="Email"
            value={credentials.email}
            onChange={handleInputChange}
          />
          <br />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={credentials.password}
            onChange={handleInputChange}
          />
          <br />
          <button type="button" onClick={login}>
            Login
          </button>
        </div>
      ) : (
        <div>
          <h2>Chat</h2>
          <p>Signed in!</p>
        </div>
      )}
    </div>
  );
}
```

